con:config.script:
def props = testRunner.testCase.getTestStepByName("Properties")
startimei = props.getPropertyValue("startimei").toLong()
threadvol = props.getPropertyValue("threadvol").toInteger()
imeifrom = props.getPropertyValue("imeifrom"+context.ThreadIndex).toLong()
imeito = props.getPropertyValue("imeito"+context.ThreadIndex).toLong()
threadidx = context.ThreadIndex.toInteger()
startimeilocal = startimei+threadvol*threadidx
if((imeifrom+10) < (startimeilocal+threadvol))
{
props.setPropertyValue("imeifrom"+context.ThreadIndex,(imeifrom+10).toString())
props.setPropertyValue("imeito"+context.ThreadIndex,(imeito+10).toString())
testRunner.gotoStepByName("Create-equipments")
}
