// get properties
def props = testRunner.testCase.getTestStepByName( "Properties" )

// read file
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def csvFilePath = context.expand('${projectDir}/data/data.txt')
context.fileReader = new BufferedReader(new FileReader(csvFilePath))
lines =context.fileReader.readLines()

//global variable
props.setPropertyValue("linenum","0")
props.setPropertyValue("data",lines.toString().replace('[','').replace(']',''))
props.setPropertyValue("maxline",lines.size().toString())

// get properties
def props = testRunner.testCase.getTestStepByName( "Properties" )
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

// read line
String result = props.getPropertyValue("data");
rowsData = result.split(",")
//log.info(rowsData[0])
linenum = Integer.parseInt(props.getPropertyValue("linenum"));
log.info(linenum)
String[] line = rowsData[linenum].split(";")
String imsi = line[0].replace(" ","")
String msisdn = line[1].replace(" ","")
String key = line[2].replace(" ","")
log.info(imsi)

//global variable
props.setPropertyValue("userId","imsi-"+imsi)
props.setPropertyValue("imsi",imsi)
props.setPropertyValue("msisdn",msisdn)
props.setPropertyValue("key",key)

linenum++
props.setPropertyValue("linenum",linenum.toString())

// get properties
def props = testRunner.testCase.getTestStepByName( "Properties" )
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

