rowsData = rowsData.replace('[','').replace(']','').replace(' ','')

// set properties

props.setPropertyValue("lineNum"+context.ThreadIndex,"0")
props.setPropertyValue("maxLine"+context.ThreadIndex,lines.size().toString())
props.setPropertyValue("data"+context.ThreadIndex,rowsData)

//log.info('loaded'+csvName +'data in '+context.ThreadIndex)

// get properties
